function create_noticebox_shortcode($atts) {
	// Attributes
	$atts = shortcode_atts(
		array(
			'msg' 		=> '',
			'txt' 		=> '',
			'text' 		=> '',
			'message' 	=> '',
			'type' 		=> '',
			'class'		=> '',
		),
		$atts,
		'noticebox'
	);
	// message 
	$msg = $atts['msg'];
	$txt = $atts['txt'];
	$text = $atts['text'];
	$message = $atts['message'];
	
	// type
	$type = $atts['type'];
	// class name
	$class = $atts['class'];

	// OUTPUT
	$htmlbox = '<div class="noticebox '. $type .' '. $class. ' "><span>'. $type .': </span>'. $msg . $txt . $text . $message. '</div>';
	
	return $htmlbox;
}
add_shortcode( 'noticebox', 'create_noticebox_shortcode' );

